mapper.select:
SELECT COUNT(*) FROM BOARD_POST WHERE STATUS = 'Y'
AND CATEGORY = #{category}
AND POST_CONTENT LIKE '%${keyword}%'

mapper.update:
UPDATE BOARD_POST
SET COUNT = COUNT + 1
WHERE POST_NO = #{postNo}

UPDATE 

	BOARD_POST
	BOARD_REPLY

SET

	POST_TITLE = #{postTitle}, POST_CONTENT = #{postContent}
	REPLY_CONTENT = #{replyContent}

WHERE

	POST_NO
	REPLY_NO

  = #{refNo}
